mapper.update:
UPDATE Payment
SET
    canceledAt = #{canceledAt},
    failReason = #{failReason},
    cancelReceiptURL = #{cancelReceiptURL}
WHERE payNo = #{payNo}

mapper.select:
SELECT IFNULL(SUM(payAmount), 0) FROM Payment
WHERE canceledAt IS NULL
  AND DATE(payDate) = #{date}

SELECT IFNULL(SUM(payAmount), 0) FROM Payment
WHERE canceledAt IS NULL
  AND DATE(payDate) BETWEEN DATE_ADD(#{date}, INTERVAL -1 MONTH) AND #{date}

SELECT IFNULL(SUM(payAmount), 0) FROM Payment
WHERE canceledAt IS NULL
  AND DATE(payDate) BETWEEN DATE_ADD(#{date}, INTERVAL -1 YEAR) AND #{date}
